import React,{useEffect} from 'react';
import {Link, useLocation, useNavigate} from "react-router-dom";
import "../style/Navbar.css"

const Navbar = () => {
    let location=useLocation();
    const navigate=useNavigate()
    useEffect(()=>{
        
    },[location]);

    const logout=()=>{
        localStorage.removeItem('token')
        navigate('/login');
    }
    return (
        <>
            <nav className="navbar navbar-expand-lg navbar-dark bg-dark">
                <Link className="navbar-brand" to="/">Notepie</Link>
                <button className="navbar-toggler" type="button" data-toggle="collapse" data-target="#navbarSupportedContent" aria-controls="navbarSupportedContent" aria-expanded="false" aria-label="Toggle navigation">
                    <span className="navbar-toggler-icon"></span>
                </button>

                <div className="collapse navbar-collapse d-flex" id="navbarSupportedContent">
                    <ul className="navbar-nav mr-auto">
                        <li className="nav-item ">
                            <Link className={`nav-link ${location.pathname==="/"?"active": " "}`} to="/">Home </Link>
                        </li>
                        <li className="nav-item">
                            <Link className={`nav-link ${location.pathname==="/about"?"active": " "}`} to="/about">About</Link>
                        </li>
                    </ul>
                   {!localStorage.getItem('token')? <form className="d-flex ">
                    <div className="verify">
                    <Link className="btn-login" to="/login" role="button">Login</Link>
                    <Link className="btn-signup" to="/signup" role="button">SignUp</Link>
                    </div>

                    </form>: <button onClick={logout} className='logout'> Logout</button>}
                </div>
            </nav>
        </>
    )
}

export default Navbar
