import React, { useState } from "react";
import noteContext from "./noteContext";

const NoteState = (props) => {
  const host="http://localhost:5000"
  const initialState = []

  const [notes, setNotes] = useState(initialState);

  //get all notes
  const getNotes = async () => {
    //api call
    const response = await fetch(`${host}/api/notes/fetchallnotes`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjVmMDZjMGJiZTRlZjk2M2E3ODFmOTgzIn0sImlhdCI6MTcxMDI1NTI3NH0.02eC5cO9JsurbVV49FAO4O4SpSzYdNEtknFEHSb2RWg",
        
      },
    });
    const json= response.json();
    console.log(json)
 };



  //add a note
  
  const addNote = async (title, description, tag) => {
    //api call
    const response = await fetch(`${host}/api/notes/addnote`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjVmMDZjMGJiZTRlZjk2M2E3ODFmOTgzIn0sImlhdCI6MTcxMDI1NTI3NH0.02eC5cO9JsurbVV49FAO4O4SpSzYdNEtknFEHSb2RWg",
        
      },
      body: JSON.stringify({title,description,tag}),
    });
    const json= response.json();



    console.log("adding a new note");
    const note = {
      _id: "65f0a63665976d75b3e5df354",
      user: "65f06c0bbe4ef963a781f9834",
      title: title,
      description: description,
      tag: tag,
      date: "2024-03-12T19:00:06.142Z",
      __v: 0,
    };
    setNotes(notes.concat(note));
  };

  //delete a note
  const deleteNote = (id) => {
    const newNotes = notes.filter((note) => {
      return note._id !== id;
    });
    setNotes(newNotes);
  };

  //edit a note
  const editNote = async (id, title, description, tag) => {
    //api call
    const response = await fetch(`${host}/api/notes/updatenote/${id}`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json",
        "auth-token":"eyJhbGciOiJIUzI1NiIsInR5cCI6IkpXVCJ9.eyJ1c2VyIjp7ImlkIjoiNjVmMDZjMGJiZTRlZjk2M2E3ODFmOTgzIn0sImlhdCI6MTcxMDI1NTI3NH0.02eC5cO9JsurbVV49FAO4O4SpSzYdNEtknFEHSb2RWg",
        
      },
      body: JSON.stringify({title,description,tag}),
    });
    const json= response.json();

    for (let index = 0; index < notes.length; index++) {
      const element = notes[index];
      if (element._id === id) {
        element.title = title;
        element.description = description;
        element.tag = tag;
      }
    }
  };

  return (
    <noteContext.Provider value={{ notes, addNote, deleteNote, editNote,getNotes }}>
      {props.children}
    </noteContext.Provider>
  );
};

export default NoteState;
