import React from 'react'

const Signup = () => {
    
  const navigate = useNavigate();
  const[credentials,setCredentials]=useState({email:"",password:""})

  const handlesubmit =async (e)=>{
       e.preventDefault();
      const response = await fetch('http://localhost:5000/api/auth/login', {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          
        },
        body: JSON.stringify({email: credentials.email,password:credentials.password}),
       });
  
       const json=await response.json();

      console.log(json);
      if(json.success){
        //
        localStorage.setItem('token',json.authtoken)
        navigate('/');

      }
      else{
        alert('wrong credential')
      }
  }

  const onChange=(e)=>{
    setCredentials({...credentials,[e.target.name]:e.target.value})
  }



  return (
  
    <div>
    <form onSubmit={handlesubmit}>
        <div className="form-group">
          <label htmlFor="name">Name</label>
          <input type="text" className="form-control" id="name" name="name" value={credentials.email} onChange={onChange} aria-describedby="emailHelp" placeholder="Enter email" />
        </div>
        <div className="form-group">
          <label htmlFor="email">Email address</label>
          <input type="email" className="form-control" id="email" name="email" value={credentials.email} onChange={onChange} aria-describedby="emailHelp" placeholder="Enter email" />
          <small id="emailHelp" className="form-text text-muted">We'll never share your email with anyone else.</small>
        </div>
        <div className="form-group">
          <label htmlFor="password">Password</label>
          <input type="password" className="form-control" id="password" name='password' value={credentials.password} onChange={onChange} placeholder="Password" />
        </div>
        <div className="form-group">
          <label htmlFor="cpassword">Confirm Password</label>
          <input type="password" className="form-control" id="cpassword" name='cpassword' value={credentials.password} onChange={onChange} placeholder="Password" />
        </div>
        <div className="form-check">
        </div>
        <button type="submit" className="btn btn-primary" >Submit</button>
      </form>
    </div>
    
  )
  
}

export default Signup
