import React, { useContext, useEffect, useRef,useState } from "react";
import noteContext from "../context/noteContext";
import Noteitem from "./Noteitem";
import Addnote from "./Addnote";

const Notes = () => {
  const context = useContext(noteContext);
  const { notes, getNotes } = context;
  const [note,setNote]=useState({etitle:"",edescription:"",etag:"default"})

  useEffect(() => {
    getNotes();
  }, []);

  
  const updateNote = (currentNote) => {
    ref.current.click()
    setNote({etitle:currentNote.etitle,edescription:currentNote.description,etag:currentNote.tag})
  };

  const ref = useRef(null)

  const onClick=(e)=>{
    e.preventDefault();
    //  addNote(note.title,note.description,note.tag);
}

const onChange=(e)=>{
  setNote({...note,[e.target.name]:e.target.value})
}

  return (
    <div>
      <Addnote />
      <button type="button" ref={ref} className="btn btn-primary d-none" data-bs-toggle="modal" data-bs-target="#exampleModal">
        Launch demo modal
      </button>
      <div className="modal fade" id="exampleModal" tabIndex="-1" aria-labelledby="exampleModalLabel"  aria-hidden="true">
        <div className="modal-dialog">
          <div className="modal-content">
            <div className="modal-header">
              <h5 className="modal-title" id="exampleModalLabel">Edit Note</h5>
              <button type="button" className="btn-close" data-bs-dismiss="modal" aria-label="Close"></button>
            </div>
            <div className="modal-body">
            <form>
                    <div className="form-group mx-10">
                        <label htmlFor="etitle">Title</label>
                        <input type="text" className="form-control" id="etitle" name="etitle" value={note.etitle} aria-describedby="emailHelp" onChange={onChange} />
                    </div>
                    <div className="form-group ">
                        <label htmlFor="edescription">Description</label>
                        <input type="text" className="form-control" id="edescription" name="edescription" value={note.edescription} onChange={onChange} />
                    </div>
                    <div className="form-group ">
                        <label htmlFor="etag">tag</label>
                        <input type="text" className="form-control" id="etag" name="etag" value={note.etag} onChange={onChange} />
                    </div>
                   
                </form>
            </div>
            <div className="modal-footer">
              <button type="button" className="btn btn-secondary" data-bs-dismiss="modal">Close</button>
              <button type="button" className="btn btn-primary" onClick={onClick}>Save changes</button>
            </div>
          </div>
        </div>
      </div>

      <div className="row my-3">
        <h2>your notes</h2>
        {notes.map((note) => {
          return (
            <Noteitem key={note._id} updateNote={updateNote} note={note} />
          );
        })}
      </div>
    </div>
  );
}; 

export default Notes;
